mapper.select:
		SELECT
		a.id as id,
		a.NAME as name,
		a.card_no as cardNo,
		a.telephone as telephone,
		a.school_id as schoolId,
		a.dorm_id as dormId,
		a.is_wb as isWb,
		a.is_success as isSuccess,
		a.user_id as userId,
		a.create_time as createTime,
		a.wb_is_success as wbIsSuccess,
		a.is_protocol as isProtocol,
		a.is_pay as isPay,
		b.name  as schoolName
		FROM
		p_student_info a
		LEFT JOIN sys_dept b
		ON
		a.school_id = b.dept_id

			 and a.id = #{id} 
			 and a.name  like CONCAT('%',#{name},'%')
			 and a.card_no = #{cardNo} 
			 and a.telephone = #{telephone} 
			 and a.school_id = #{schoolId} 
			 and a.dorm_id = #{dormId} 
			 and a.is_wb = #{isWb} 
			 and a.is_protocol = #{isProtocol} 
			 and a.wb_is_success = #{wbIsSuccess} 
			 and a.is_success = #{isSuccess} 
			 and a.user_id = #{userId} 

				and a.create_time >= #{beginTime}


				and a.create_time <= #{endTime}

			 and a.is_pay = #{isPay} 
			 and a.user_id in

					#{item}





                order by ${sort} ${order}


                order by a.id desc



			limit #{offset}, #{limit}

select count(*) from p_student_info

  		   and id = #{id} 
  		   and name = #{name} 
  		   and card_no = #{cardNo} 
  		   and telephone = #{telephone} 
  		   and school_id = #{schoolId} 
  		   and dorm_id = #{dormId} 
  		   and is_wb = #{isWb} 
	  and is_protocol = #{isProtocol} 
	 		 and wb_is_success = #{wbIsSuccess} 
  		   and is_success = #{isSuccess} 
  		   and user_id = #{userId} 

			 and create_time >= #{beginTime}


			 and create_time <= #{endTime}

		  and is_pay = #{isPay} 
		  and user_id in

				 #{item}

mapper.update:
update p_student_info
name = #{name}, card_no = #{cardNo}, telephone = #{telephone}, school_id = #{schoolId}, dorm_id = #{dormId}, is_wb = #{isWb}, is_success = #{isSuccess}, user_id = #{userId}, create_time = #{createTime}, is_pay = #{isPay}, wb_is_success = #{wbIsSuccess}, is_protocol = #{isProtocol},
where id = #{id}
